        List<ServiceShow> serviceShowList = new ArrayList<>();
        Map<String, Integer> tmpMap = new HashMap<>();
        int count = 0;
        System.out.println(ids.length);
        for (Integer id : ids) {
            ServiceTotal serviceTotal = serviceTotalMapper.selectById(id);

            //创建分词对象
            StringReader reader = new StringReader(serviceTotal.getServiceDetail());
            //分词
            IKAnalyzer analyzer = new IKAnalyzer(true);
            TokenStream ts = analyzer.tokenStream("", reader);
            CharTermAttribute term = ts.getAttribute(CharTermAttribute.class);
            ts.reset();
            //遍历分词数据
            Set<Integer> wordSet=new HashSet<>();
            while (ts.incrementToken()) {
                if (tmpMap.containsKey(term.toString())) {
                    wordSet.add(tmpMap.get(term.toString()));
                } else {
                    tmpMap.put(term.toString(), count);
                    wordSet.add(count);
                    count++;
                }
            }
            ts.close();
            reader.close();

            ServiceShow serviceShow = new ServiceShow();
            BeanUtils.copyProperties(serviceTotal, serviceShow);
            serviceShow.setDetectionItemId(wordSet.toString().substring(1,wordSet.toString().length()-1));
            serviceShowList.add(serviceShow);
        }
//        File file = new File("data_item.json");
